export default function parseMultipartBoundary(response) {
  var contentType = response.headers.get('Content-Type');
  var matches = contentType.match(/(.*);\s*boundary="(.*)"/);

  if (matches && matches[1] && matches[2]) {
    return {
      contentType: matches[1],
      boundary: matches[2],
    };
  }

  return {
    contentType,
  };
}

